import React, { useState, useEffect, useRef } from "react";
import Headermobile from "./Headermobile";
import Footer from "./Footer";
import { Link, useParams } from "react-router-dom";
import { baseurl } from "../Component/Baseurl";
import MM1 from "../images/MM1.webp";
import MM2 from "../images/MM2.webp";
import HH from "../images/Horoscope.webp";

import M1 from "../images/M1.webp";
import M2 from "../images/M2.webp";
import M3 from "../images/M3.webp";

function Dynamicpage() {
  const [rechargeData, setRechargeData] = useState(null);
  const { shortcode } = useParams();
  const tableRef = useRef(null);

  const fetchDataFromApi = async () => {
    try {
      const response = await fetch(`${baseurl}/rcpt-api/homepageold`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json", // Adjust the content type as needed
        },
        // You can send an empty object in the request body or omit it if not needed.
        body: JSON.stringify({ shortcode }),
      });

      if (response.ok) {
        const responseData = await response.text();
        // console.log(responseData);
        // window.location.href = 'https://www.rcpt.in'; // Replace with the URL you want to navigate to
      } else {
        console.error("API request failed");
      }
    } catch (error) {
      console.error("An error occurred", error);
    }
  };

  useEffect(() => {
    (window.adsbygoogle = window.adsbygoogle || []).push({});

    const apiEndpoint = `${baseurl}/rcpt-api/getrechargedetailsold?shortcode=${shortcode}`;
    const requestOptions = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
    };

    fetch(apiEndpoint, requestOptions)
